import React, {useState} from "react";
import ReactDOM from "react-dom";
import css from "./index.css";

const oneSecond = 1000; // one second same as 1000ms
const counterTimer = 25 * 60 ; //timedown minutes
const mm = Math.floor(counterTimer / 60);//minutes
const ss = Math.floor(counterTimer % 60);//seconds

const [Timer , setTimer] = useStates(
    setTimeout=Timer--,
)

class App extends React.Component {
  render() {
    return (
      <div className="timer">
        <span>{String(mm).padStart(2 , '0')}</span>
        <span>:</span>
        <span>{String(ss).padStart(2 , '0')}</span>
      </div>
    );
  }
}

export default App
